import { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

//SQUARES IN THE BOARD
function Square(props) {
  return (
    <button className="square" >
      {props.value}
    </button>
  );
}

function ColSquare(props) {
  return (
    <button className="square col-square" >
      {props.value}
    </button>
  );
}

//Boards for each individual player 
class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(25).fill(null),
      currentPlayer: 1,
    };
  }

  renderSquare(i) {
    return (
      <Square
        value={this.state.squares[i]}
      />
    );
  }

  renderColSquare(i) {
    return (
      <ColSquare
        value={this.state.squares[i]}
      />
    );
  }

  render() {
    //todo: there's gotta be a better way than this 
    return (
      <div>
        <div className="board-row">
          {this.renderColSquare("L1")}
          <button className="empty-square"/ >
          <button className="empty-square"/ >
          <button className="empty-square"/ >
          {this.renderColSquare("L0")}
        </div>
        <div className="board-row">
          {this.renderColSquare("LL1")}
          {this.renderColSquare("L1")}
          <button className="empty-square"/ >
          {this.renderColSquare("R1")}
          {this.renderColSquare("RR1")}
        </div>
        <div className="board-row">
          {this.renderColSquare("LL0")}
          {this.renderColSquare("L0")}
          {this.renderColSquare("M0")}
          {this.renderColSquare("R0")}
          {this.renderColSquare("RR0")}
        </div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
          {this.renderSquare(3)}
          {this.renderSquare(4)}
        </div>
        <div className="board-row">
          {this.renderSquare(5)}
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
          {this.renderSquare(9)}
        </div>
        <div className="board-row">
          {this.renderSquare(10)}
          {this.renderSquare(11)}
          {this.renderSquare(12)}
          {this.renderSquare(13)}
          {this.renderSquare(14)}
        </div>
        <div className="board-row">
          {this.renderSquare(15)}
          {this.renderSquare(16)}
          {this.renderSquare(17)}
          {this.renderSquare(18)}
          {this.renderSquare(19)}
        </div>
        <div className="board-row">
          {this.renderSquare(20)}
          {this.renderSquare(21)}
          {this.renderSquare(22)}
          {this.renderSquare(23)}
          {this.renderSquare(24)}
        </div>
      </div>
    );
  }
}


function Ficha(props){
  return(
    <div className="ficha"
      style={props.style}
      onClick = {props.onclick}>
    <p> {props.simbolo} </p>
    </div>
  )
}


function Canasta(props){
  return(
    <div className="canasta"> 
      {props.fichas}
    </div>
  )
}


//Will store information on currently selected subset, eventually 
// also player order
class Player extends React.Component{
  constructor(props) {
    super(props);
    this.fichasEnCanasta = []
    for(const ficha in this.props.fichas){
      let color = this.props.fichas[ficha]["color"];
      let style = {backgroundColor:color};
      let simbolo = this.props.fichas[ficha]["patron"];
      this.fichasEnCanasta.push(<Ficha style = {style} simbolo = {simbolo} onclick = {() => this.elegirSubconjunto({color: color, patron: simbolo})}/>);
    }
    console.log(this.fichasEnCanasta);
    this.state = {
      subset: [], // Si concuerda con cualquier cosa en el subconjunto
    }
  }

  fillFichas(){
    console.log(this.fichasEnCanasta);
  }

  acuerda(ficha){
    return(this.state.subset.includes(ficha["patron"]) || this.state.subset.includes(ficha["color"]));
  }

//TODO: THERS GOTTA be a better way to do this
//TODO this is pseudocode. eventually, highlight the fichas que todavia se pueden elegir
  elegirSubconjunto(ficha){
    //Si no se ha eligido una ficha o si la ficha no acuerda
    if (this.state.subset.length == 0 || !this.acuerda(ficha)){
      //TODO: Make sure I'm copying arrays right
      this.state.subset = [ficha["color"], ficha["patron"]]; 
    } else {
        if(this.state.subset.length == 2) { //Solo se ha elegido una ficha
          if(this.state.subset.includes(ficha["patron"])){
            if(!this.state.subset.includes(ficha["color"])){ //Si NO son identicas
              this.state.subset = [ficha["patron"]];
            } 
          } else { //Como concuerdan, no se tiene que checar si tienen el mismo color
            this.state.subset = [ficha["color"]];
          }
        } else if (this.state.subset.length == 1){ // Ya se han elegido >1 fichas no identicas, y la nueva elegida concuerda
            return; // no hay nada mas que hacer
        } else {
          console.log("ERROR App.js 184")
        }
      }
    console.log(`Subset selected: ${this.state.subset}`)
    for(var i = 0; i<this.props.fichas.length; i++){
        let color = this.props.fichas[i]["color"];
        var style = {};
        if(this.acuerda(this.props.fichas[i])){
          style = {backgroundColor:color, border: '10 solid black'}
        } else {
          style = {backgroundColor:color}
        }
        this.fichasEnCanasta[i] = <Ficha style={style} 
                                    simbolo = {this.props.fichas[i]["patron"]} 
                                    onClick={() => this.elegirSubconjunto({color: this.props.fichas[i]["color"],
                                      patron:this.props.fichas[i]["patron"] })} />;
      }
    }


  render(){
    return(
      <div>
        <Board />
        <Canasta fichas={this.fichasEnCanasta}/>
      </div>
    )
  }
}


//Called from GameRoom to start the game. Probably not in the right place
function startGame(skt, roomname){
  console.log(`Sent request to start game`);
  skt.current.emit('start-game', roomname);
};

//Gameroom. Props inclused room and socket info
function GameRoom(props){
  console.log(`props in current room: ${props.room}`);
  var boards = [];

  const client = props.socket;
  const roomname = props.room["roomname"];
  const fichas = props.room["fichas"];


  if(fichas.length > 0){
    var idx = 0;
    for(const usr in props.room["users"]){
      boards.push(<Player fichas={props.room["users"][usr][1]}/>);
    }
  } else {
    for(const usr in props.room["users"]){
      boards.push(<Player />);
    }
  }

  return(
    <div>
      {boards}
      {fichas.length
        ? 
        <div></div>
        :
        <button onClick={() => startGame(client, roomname)}>
          Start game
        </button>
      }
    </div>
  )
}

//Connect to server info 
function handleClick(skt){
    var form = document.getElementById('room-creation-form');
    var input = document.getElementById('new-room-name');

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      if (input.value) {
        skt.current.emit('new-room', input.value);
      }
    });

    console.log(`Trying to create or join room: ${input.value}`)

  };


function App() {
  const socket = io('http://localhost:5000');
  const [gameRoom, setGameRoom] = useState('');
  const client = useRef();

  useEffect(() => {

    socket.on('connect', () => console.log(`Client connected: ${socket.id}`));

    socket.on('disconnect', (reason) =>
      console.log(`Client disconnected: ${reason}`)
    );

    socket.on('connect_error', (reason) =>
      console.log(`Client connect_error: ${reason}`)
    );

    socket.on('room-created', (msg) => setGameRoom(msg));

    socket.on('new-user', (msg) => setGameRoom(msg));

    socket.on('game-started', (msg) => setGameRoom(msg));

    client.current = socket;
  }, []);



  return (
    <div>
        {gameRoom
          ?
            <div color = "blue">
              <p> In room {gameRoom.roomname} </p>
              <GameRoom room={gameRoom} socket={client} /> 
            </div>
          :
            <div>
              <p> Create a new game room </p>
              <form id="room-creation-form" action="">
                <input id="new-room-name" autocomplete="off" />
                <button onClick={() => handleClick(client)}>
                  Send
                </button>
              </form>
            </div>
        }
      </div>
  );
}

export default App;
