import { io } from 'socket.io-client';
import Jugador from './Jugador';
import ItemTypes from './ItemTypes'
import React from 'react';
import { useEffect, useState, useRef } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import'./sala.css';
import {DndProvider, useDrag, useDrop} from 'react-dnd'
import{HTML5Backend} from 'react-dnd-html5-backend'

const fs = require('./server/funcionesServidor.js');


//Sala. Props inclused sala and socket info
//TODO: Fix everything related to "espera", incl the fact you can play during it
const Sala = ({sala, socket, setSala, setTurno, setRonda}) => {
  console.log(sala)
  console.log(`Se actualizo la sala de juego`)
  var jugadores = [];

  const client = socket;
  var espera = ""
  var usr_id = 0 //La posicion del jugador en la sala

  useEffect(() => {
    console.log(sala)
  }, [sala])

  const comenzar = () =>{
    if(!sala["local"]){
      console.log(`Se mando un request para comenzar el juego`);
      skt.current.emit('comenzar-juego', sala["sala"]);
    } else {
      sala["fichas"] = fs.crearFichas()
      fs.distribuirFichas(sala, 0)
      setSala(sala)
      setturno(0)
      setRonda(0)
    }
  }

  if(sala["activo"]){
    for(const usr in sala["usuarios"]){
      console.log(sala["usuarios"][usr]["usuario"])
      console.log(client.current.id)
      if(sala["usuarios"][usr]["usuario"] == client.current.id){
        jugadores.push(<Jugador jugador = {usr}
                              activo = {true}
                              sala = {sala}
                              socket = {client}
                              key = {sala["usuarios"][usr]["usuario"]}/>)
      } else {
        jugadores.push(<Jugador jugador = {usr}
                              activo = {false}
                              sala = {sala}
                              socket = {client}
                              key = {sala["usuarios"][usr]["usuario"]}/>)
      }
    }
  } else {
    for(const usr in sala["usuarios"]){
      jugadores.push(<Jugador jugador = {usr}
                              sala = {sala} 
                              key={sala["usuarios"][usr]["usuario"]}/>);
    }
  }

  if(sala["espera"]){
    espera = "espera"
  }

  return(
    <DndProvider backend={HTML5Backend}>
    <div> 
      <h1>Sala de juego "{sala["sala"]}"</h1>
      <div id = {espera}> </div>
      <div class ="sala">
        {sala["activo"]
          ? 
            <div class="jugadores" key="boards1">{jugadores}</div>
            :
            <div>
            <div class="jugadores" key="boards2"> 
            {jugadores}
            </div>
            <div id="comenzarBtn">
              <button onClick={comenzar}>
                Comenzar
              </button>
            </div>
          </div>
        }
      </div>
    </div>
    </DndProvider>
  )
}

export default Sala;
