import {DndProvider, useDrag, useDrop} from 'react-dnd'
import imagenes_fichas from './Assets/imagenes.js'

const ItemTypes = {
  FICHA: 'Ficha'
}

function sacarImagen(color, simbolo){
  console.log(imagenes_fichas.imagenes_fichas["otomi"]["rojo"][2])
  return imagenes_fichas.imagenes_fichas["otomi"]["rojo"][2]
}

function Ficha(props){
  const[{isDragging}, drag] = useDrag(() => ({
    type: ItemTypes.FICHA,
    item: () => props.onclick(),
    collect: monitor => ({
      isDragging: !!monitor.isDragging(),
    }),
  }))

  return(
    <div
      ref = {drag} 
      className="ficha"
      style={{opacity: isDragging? 0.5: 1}, {backgroundImage:sacarImagen(props.color, props.simbolo)}}
      onClick = {props.onclick}>
    <p> <img src = "./Assets/trama.png" />{props.simbolo} </p>
    </div>
  )
}

export default Ficha;
