import { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import React from 'react';
import ReactDOM from 'react-dom/client';
import Sala from './Sala'
import './index.css';
import {DndProvider, useDrag, useDrop} from 'react-dnd'
import{HTML5Backend} from 'react-dnd-html5-backend'

//Connect to server info 
//TODO: After clicking button, set button to NOT CLICK

function App() {
  var socket = ""
  const [sala, setSala] = useState('');
  const [salaLlena, setSalaLlena] = useState('');
  var client = useRef();

  const toggleSala = () =>{
    setSala({
      usuarios: [], 
      sala: "Local",
      fichas: [], //Diccionario de fichas
      fichasCentro: [], // Fichas que se pueden jugar
      ronda: 0,
      turno: 0,
      activo: false,
      primerTurno: true, //Para manejar requests multiples para terminar una ronda 
      espera: false //Para manejar desconecciones 
    })
  }

  const handleClick = () => {
    socket = io('http://localhost:5000');
    client = useRef();
    var form = document.getElementById('room-creation-form');
    var input = document.getElementById('new-room-name');

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      if (input.value) {
        socket.emit('nueva-sala', input.value);
      }
    });

    console.log(`Tratando de crear o unirse a la sala: ${input.value}`)
  }

  useEffect(() => {
    if(socket){
      socket.on('connect', () => console.log(`Se conecto el usuario: ${socket.id}`));

      socket.on('disconnect', (reason) =>
        console.log(`Se desconecto el usuario: ${reason}`)
      );

      socket.on('connect_error', (reason) =>
        console.log(`Error de coneccion: ${reason}`)
      );

      socket.on('sala-inicializada', (msg) => setSala(msg));

      socket.on('sala-llena', () => setSalaLlena(true));

      socket.on('nuevo-usuario', (msg) => setSala(msg));

      socket.on('juego-inicializado', (msg) => {
        console.log("Comenzo el juego");
        setSala(msg)});

       socket.on('hacer-jugada', (msg) => {
        console.log("Se hizo una jugada");
        setSala(msg)});

       socket.on('siguiente-ronda', (msg) =>{
        console.log("Bajando las fichas y sacando puntuacion ...")
        setSala(msg)});

       socket.on('final', (msg) =>{
        console.log("Se acabo el juego")
        setSala(msg)});

      socket.on('en espera', (msg) => {
        console.log("Esperando la reconneccion del jugador ... ")
        setSala(msg)});

      socket.on('Desconeccion de jugador', (msg) => setSala(msg));

      client.current = socket;
    }
  }, []);



  return (
    <div>
        {sala
          ?
            <div>
              <Sala sala={sala} socket={client} /> 
            </div>
          :
            <div>
              <p> Create a new game room </p>
              <form id="room-creation-form" action="">
                <input id="new-room-name" autocomplete="off" />
                <button onClick={() => handleClick(client)}>
                  Send
                </button>
              </form>
              <button onClick={toggleSala}>
                  Sala de multiplayer local
              </button>
              {salaLlena ? <p> Room full or already in game, try another </p> : <div/>}
            </div>
        }
      </div>
  );
}

export default App;
