import React from 'react';
import ItemTypes from './ItemTypes'
import {DndProvider, useDrag, useDrop} from 'react-dnd'
import v2_i from './Assets/vara1_izq.png';
import v12 from './Assets/vara1_4.png';
import v22 from './Assets/vara1_3.png';
import v32 from './Assets/vara1_2.png';
import v2_d from './Assets/vara1_der.png';

import v21 from './Assets/vara2_2.png';

function Square(props) {
  return (
    <div className="square" style = {props.style}>
      {props.value}
      <p>x</p>
    </div>
  );
}

function alertfx(){
  alert("drop")
  return undefined
}

function ColSquare(props) {
  const[{isOver}, drop] = useDrop(() => ({
    accept: ItemTypes.FICHA,
    drop: (item, monitor) => props.ondrop(monitor.getItem()),
    collect: mon => ({
      isOver:!!mon.isOver(),
    }),
  }))
  return (
    <div 
      ref={drop}
    >
    <div className="square col-square" onClick = {props.onclick}>
      {props.value}
      <p>x</p>
    </div>
    {isOver && (
      <div  />
      )}
    </div>
  )
};


//Boards for each individual player 
class Tablero extends React.Component {
  constructor(props) {
    super(props);
  }

  //TODO: DONT RUN FOR EVERY FICHA, ONLY THE ONES THAT HAVE BEEN DROPPED!
  highlight(i){
    const col = Math.floor(i/5)
    const fila = i%5
    var style = ""
    if(this.props.puntuacion[0][col].length){
      if((this.props.puntuacion[0][col][0] <= fila) && (this.props.puntuacion[0][col][1] >= fila)){
        style = "red"
      }
    }
    for(const sc in this.props.puntuacion[1][fila]){
      if(this.props.puntuacion[1][fila][sc].length){
        if((this.props.puntuacion[1][fila][sc][0] <= col) && (this.props.puntuacion[1][fila][sc][1] >= col)){
          if(style == "red"){
            style = "purple"
          } else {
            style = "blue"
          }
        }
      }
    }
    return {backgroundColor: style}
  }

  renderSquare(i) {
    if(this.props.tableroFichas){
      if(this.props.puntuacion.length){
        return (
          <Square
            value={this.props.tableroFichas[i]}
            style={this.highlight(i)}
          />
        );
      } else {
        return (
          <Square
            value={this.props.tableroFichas[i]}
            style={null}
          />
        );
      }
    } else {
      return (
        <Square
          value={null}
          style = {null}
        />
      );
    }
  }

  renderColSquare(col, fil) {
    return (
      <ColSquare
        value={this.props.cols[col][fil]}
        onclick = {() => this.fichaEnColumna(col, this.props.ficha)}
        ondrop = {(f) => this.dndFichaEnColumna(col, f)}
      />
    );
  }

  fichaEnColumna(col, ficha){
    this.props.ponerFicha(col, ficha)
  }

  dndFichaEnColumna(col, ficha){
    this.props.dndFicha(col, ficha)
  }

  render_conserved() {
    //todo: there's gotta be a better way than this 
    return (
      <div>
        <div className="board-row">
          <button className="empty-square"/ >
          {this.renderColSquare(0, 2)}
          <button className="empty-square"/ >
          <button className="empty-square"/ >
          <button className="empty-square"/ >
          {this.renderColSquare(4, 2)}
          <button className="empty-square"/ >
        </div>
        <div className="board-row">
          <button className="empty-square"/ >
          {this.renderColSquare(1, 1)}
          {this.renderColSquare(2, 1)}
          {this.renderColSquare(3, 1)}
          <button className="empty-square"/ >
        </div>
        <div className="board-row">
          {this.renderColSquare(0, 0)}
          {this.renderColSquare(1, 0)}
          {this.renderColSquare(2, 0)}
          {this.renderColSquare(3, 0)}
          {this.renderColSquare(4, 0)}
        </div>
        <div className="board-row">
          {this.renderSquare(4)}
          {this.renderSquare(9)}
          {this.renderSquare(14)}
          {this.renderSquare(19)}
          {this.renderSquare(24)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(8)}
          {this.renderSquare(13)}
          {this.renderSquare(18)}
          {this.renderSquare(23)}
        </div>
        <div className="board-row">
          {this.renderSquare(2)}
          {this.renderSquare(7)}
          {this.renderSquare(12)}
          {this.renderSquare(17)}
          {this.renderSquare(22)}
        </div>
        <div className="board-row">
          {this.renderSquare(1)}
          {this.renderSquare(6)}
          {this.renderSquare(11)}
          {this.renderSquare(16)}
          {this.renderSquare(21)}
        </div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(5)}
          {this.renderSquare(10)}
          {this.renderSquare(15)}
          {this.renderSquare(20)}
        </div>
      </div>
    );
  }
  render() {
    //todo: there's gotta be a better way than this 
    return (
      <div>
        <div className="board-row">
          <div class="square" style={{backgroundImage: `url(${v2_i})`}}></div>
          {this.renderColSquare(0, 2)}
          <div class="square" style={{backgroundImage: `url(${v12})`}}></div>
          <div class="square" style={{backgroundImage: `url(${v22})`}}></div>
          <div class="square" style={{backgroundImage: `url(${v32})`}}></div>
          {this.renderColSquare(4, 2)}
          <Square style={{backgroundImage: `url(${v2_d})`}}/>
        </div>
        <div className="board-row">
          <Square />
          {this.renderColSquare(0, 1)}
          {this.renderColSquare(0, 1)}
          <Square style={{backgroundImage: `url(${v21})`}}/>
          {this.renderColSquare(3, 1)}
          {this.renderColSquare(4, 1)}
        </div>
        <div className="board-row">
          {this.renderColSquare(0, 0)}
          {this.renderColSquare(1, 0)}
          {this.renderColSquare(2, 0)}
          {this.renderColSquare(3, 0)}
          {this.renderColSquare(4, 0)}
        </div>
        <div className="board-row">
          {this.renderSquare(4)}
          {this.renderSquare(9)}
          {this.renderSquare(14)}
          {this.renderSquare(19)}
          {this.renderSquare(24)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(8)}
          {this.renderSquare(13)}
          {this.renderSquare(18)}
          {this.renderSquare(23)}
        </div>
        <div className="board-row">
          {this.renderSquare(2)}
          {this.renderSquare(7)}
          {this.renderSquare(12)}
          {this.renderSquare(17)}
          {this.renderSquare(22)}
        </div>
        <div className="board-row">
          {this.renderSquare(1)}
          {this.renderSquare(6)}
          {this.renderSquare(11)}
          {this.renderSquare(16)}
          {this.renderSquare(21)}
        </div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(5)}
          {this.renderSquare(10)}
          {this.renderSquare(15)}
          {this.renderSquare(20)}
        </div>
      </div>
    );
  }
}


export default Tablero;
