import { io } from 'socket.io-client';
import Jugador from './Jugador';
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import {DndProvider, useDrag, useDrop} from 'react-dnd'
import{HTML5Backend} from 'react-dnd-html5-backend'


//Called from Sala to start the game. Probably not in the right place
function startGame(skt, salaname){
  console.log(`Se mando un request para comenzar el juego`);
  skt.current.emit('comenzar-juego', salaname);
};

//Sala. Props inclused sala and socket info
//TODO: Fix everything related to "espera", incl the fact you can play during it
function Sala(props){
  console.log(`Se actualizo la sala de juego`)
  var jugadores = [];

  const client = props.socket;
  const nombre = props.sala["sala"]
  var espera = ""

  if(props.sala["activo"]){
    for(const usr in props.sala["usuarios"]){
      jugadores.push(<Jugador jugador = {props.sala["usuarios"][usr]}
                              key = {props.sala["usuarios"][usr]["usuario"]}/>)
      if(props.sala["usuarios"][usr]["canasta"]){
        var columnas = props.sala["usuarios"][usr]["canasta"].toString()
      }
    }
  } else {
    for(const usr in props.sala["usuarios"]){
      jugadores.push(<Jugador jugador = {props.sala["usuarios"][usr]} key={props.sala["usuarios"][usr]["usuario"]}/>);
    }
  }

  if(props.sala["espera"]){
    espera = "espera"
  }

  return(
    <DndProvider backend={HTML5Backend}>
    <div> 
      <p> Sala de juego "{props.sala["sala"]}"</p>
      <div id = {espera}> <p> PLEASE WAIT FOR RECONNECT </p> </div>
      {props.sala["activo"]
        ? 
        <div style={{display:"inline-flex"}} key="boards1">{jugadores}</div>
        :
        <div style={{display:"inline-flex"}} key="boards2"> 
        {jugadores}
        <button onClick={() => startGame(client, nombre)}>
          Comenzar
        </button>
        </div>
      }
    </div>
    </DndProvider>
  )
}

export default Sala;
