  constructor(props) {
    super(props);
    //Canasta 
    var enCanasta = []
    for(const ficha in this.props.fichas){
      let color = this.props.fichas[ficha]["color"];
      let style = {backgroundColor:color};
      let simbolo = this.props.fichas[ficha]["patron"];
      enCanasta.push(<Ficha style = {style} simbolo = {simbolo} onclick = {() => this.elegirSubconjunto({color: color, patron: simbolo})}/>);
    }

    //Columnas 
    const columnas = Array(5)
    columnas[0] = Array(3).fill(null)
    columnas[1] = Array(2).fill(null)
    columnas[2] = Array(1).fill(null)
    columnas[3] = Array(2).fill(null)
    columnas[4] = Array(3).fill(null)

    this.state = {
      fichaElegida: null,
      fichasEnCanasta: enCanasta,
      cols: columnas,
      subset: [], // Si concuerda con cualquier cosa en el subconjunto
    }

    console.log(this.state.fichasEnCanasta)
  }

  acuerda(ficha){
    return(this.state.subset.includes(ficha["patron"]) || this.state.subset.includes(ficha["color"]));
  }

  hacerFicha(ficha){
    let color = ficha["color"];
    let style = {backgroundColor:color};
    let simbolo = ficha["patron"];
    return <Ficha style = {style} simbolo = {simbolo} onclick = {() => this.elegirSubconjunto({color: color, patron: simbolo})}/>
  }

  ponerFicha(col, ficha){
    console.log(this.state)
    const len = this.state.cols[col].length
    const newCol = this.state.cols.map(a => {return [... a]})
    let fil = 0;
    while(fil < len && this.state.cols[col][fil]){
      console.log(this.state.cols[col][fil])
      fil++
    }
    console.log(fil)
    if(fil == len){ //Esta llena la columna 
