import { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

//SQUARES IN THE BOARD
function Square(props) {
  return (
    <button className="square" >
      {props.value}
    </button>
  );
}

function ColSquare(props) {
  return (
    <button className="square col-square" onClick = {props.onclick}>
      {props.value}
    </button>
  );
}

//Boards for each individual player 
class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(25).fill(null),
      currentPlayer: 1,
    };
  }

  renderSquare(i) {
    return (
      <Square
        value={this.state.squares[i]}
      />
    );
  }

  renderColSquare(col, fil) {
    return (
      <ColSquare
        value={this.props.cols[col][fil]}
        onclick = {() => this.fichaEnColumna(col, this.props.ficha)}
      />
    );
  }

  fichaEnColumna(col, ficha){
    this.props.ponerFicha(col, ficha)
  }

  render() {
    //todo: there's gotta be a better way than this 
    return (
      <div>
        <div className="board-row">
          {this.renderColSquare(0, 2)}
          <button className="empty-square"/ >
          <button className="empty-square"/ >
          <button className="empty-square"/ >
          {this.renderColSquare(4, 2)}
        </div>
        <div className="board-row">
          {this.renderColSquare(0, 1)}
          {this.renderColSquare(1, 1)}
          <button className="empty-square"/ >
          {this.renderColSquare(3, 1)}
          {this.renderColSquare(4, 1)}
        </div>
        <div className="board-row">
          {this.renderColSquare(0, 0)}
          {this.renderColSquare(1, 0)}
          {this.renderColSquare(2, 0)}
          {this.renderColSquare(3, 0)}
          {this.renderColSquare(4, 0)}
        </div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
          {this.renderSquare(3)}
          {this.renderSquare(4)}
        </div>
        <div className="board-row">
          {this.renderSquare(5)}
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
          {this.renderSquare(9)}
        </div>
        <div className="board-row">
          {this.renderSquare(10)}
          {this.renderSquare(11)}
          {this.renderSquare(12)}
          {this.renderSquare(13)}
          {this.renderSquare(14)}
        </div>
        <div className="board-row">
          {this.renderSquare(15)}
          {this.renderSquare(16)}
          {this.renderSquare(17)}
          {this.renderSquare(18)}
          {this.renderSquare(19)}
        </div>
        <div className="board-row">
          {this.renderSquare(20)}
          {this.renderSquare(21)}
          {this.renderSquare(22)}
          {this.renderSquare(23)}
          {this.renderSquare(24)}
        </div>
      </div>
    );
  }
}


function Ficha(props){
  return(
    <div className="ficha"
      style={props.style}
      onClick = {props.onclick}>
    <p> {props.simbolo} </p>
    </div>
  )
}


function Canasta(props){
  return(
    <div className="canasta"> 
      {props.fichas}
    </div>
  )
}


//Will store information on currently selected subset, eventually 
// also player order

//TODO/IN PROGRESS: props.fichas is stored again as "fichas", indexed. 
// Canasta and cols simply store list of index values found in each

class Player extends React.Component{
  constructor(props) {
    super(props);

    //Canasta 
    const enCanasta = []
    const myFichas = []
    for(const ficha in this.props.fichas){
      enCanasta.push(ficha)
      myFichas.push(this.props.fichas[ficha])
    }
    //Columnas 
    const columnas = Array(5)
    columnas[0] = Array(3).fill(null)
    columnas[1] = Array(2).fill(null)
    columnas[2] = Array(1).fill(null)
    columnas[3] = Array(2).fill(null)
    columnas[4] = Array(3).fill(null)

    this.state = {
      fichas: myFichas,
      fichaElegida: null,
