import { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import React from 'react';
import ReactDOM from 'react-dom/client';


//Connect to server info 
const socket = io('http://localhost:5000');

socket.on('connect', () => console.log(`Client connected: ${socket.id}`));

socket.on('disconnect', (reason) =>
  console.log(`Client disconnected: ${reason}`)
);

socket.on('connect_error', (reason) =>
  console.log(`Client connect_error: ${reason}`)
);


class Game extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      gameroom: "lol",
    };
  }


  handleClick = () => {
    var form = document.getElementById('room-creation-form');
    var input = document.getElementById('new-room-name');

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      if (input.value) {
        socket.emit('new-room', input.value);
      }
    });

    console.log(`Trying to create room: ${input.value}`)

    //TODO  I wish I could make it hang here until response ... 
    socket.on('room-created', (roomkey) =>{
      console.log(`Room created: ${roomkey}`);
    });

    this.state.gameroom = input.value;
    console.log(`State change? ${this.state.gameroom}`);
  };

  render(){
    var gameroom = this.state.gameroom
    return(
      <div>
        <p> {this.state.gameroom}</p>
        {this.state.gameroom != "lol"
          ?
            <div color = "blue">
              <p> In room {this.state.gameroom} </p>
            </div>
          :
            <div>
              <p> Create a new game room </p>
              <form id="room-creation-form" action="">
                <input id="new-room-name" autocomplete="off" />
                <button onClick={() => this.handleClick()}>
                  Send
                </button>
              </form>
            </div>
        }
      </div>
    )
  }
}

function App() {
  return (
    <>
      <Game />
    </>
  );
}

export default App;
