import { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';


function Square(props) {
  return (
    <button className="square" >
      {props.value}
    </button>
  );
}


//Boards for each individual player 
class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(25).fill(null),
      currentPlayer: 1,
    };
  }

  renderSquare(i) {
    return (
      <Square
        value={this.state.squares[i]}
      />
    );
  }

  render() {

    return (
      <div>
        <div className="board-row">
        </div>
        <div className="board-row">
        </div>
        <div className="board-row">
        </div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
          {this.renderSquare(3)}
          {this.renderSquare(4)}
        </div>
        <div className="board-row">
          {this.renderSquare(5)}
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
          {this.renderSquare(9)}
        </div>
        <div className="board-row">
          {this.renderSquare(10)}
          {this.renderSquare(11)}
          {this.renderSquare(12)}
          {this.renderSquare(13)}
          {this.renderSquare(14)}
        </div>
        <div className="board-row">
          {this.renderSquare(15)}
          {this.renderSquare(16)}
          {this.renderSquare(17)}
          {this.renderSquare(18)}
          {this.renderSquare(19)}
        </div>
        <div className="board-row">
          {this.renderSquare(20)}
          {this.renderSquare(21)}
          {this.renderSquare(22)}
          {this.renderSquare(23)}
          {this.renderSquare(24)}
        </div>
      </div>
    );
  }
}


//Gameroom
function GameRoom(props){
  console.log(`props in current room: ${props.room}`);
  var boards = [];
  for(const usr in props.room["users"]){
    boards.push(<p> <Board /></p>);
  }
  return(
    <div>
      {boards}
    </div>
  )
}

//Connect to server info 
function handleClick(skt){
    var form = document.getElementById('room-creation-form');
    var input = document.getElementById('new-room-name');

    form.addEventListener('submit', function(e) {
      e.preventDefault();
      if (input.value) {
        skt.current.emit('new-room', input.value);
      }
    });

    console.log(`Trying to create or join room: ${input.value}`)

  };


function App() {
  const [gameRoom, setGameRoom] = useState('');
  const client = useRef();

  useEffect(() => {
    const socket = io('http://localhost:5000');

    socket.on('connect', () => console.log(`Client connected: ${socket.id}`));

    socket.on('disconnect', (reason) =>
      console.log(`Client disconnected: ${reason}`)
    );

    socket.on('connect_error', (reason) =>
      console.log(`Client connect_error: ${reason}`)
    );

    socket.on('room-created', (msg) => setGameRoom(msg));

    socket.on('new-user', (msg) => setGameRoom(msg));

    client.current = socket;
  }, []);



  return (
    <div>
        {gameRoom
          ?
            <div color = "blue">
              <p> In room {gameRoom.roomname} </p>
              <GameRoom room={gameRoom} /> 
            </div>
          :
            <div>
              <p> Create a new game room </p>
              <form id="room-creation-form" action="">
                <input id="new-room-name" autocomplete="off" />
                <button onClick={() => handleClick(client)}>
                  Send
                </button>
              </form>
            </div>
        }
      </div>
  );
}

export default App;
