import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';



//TODO: DRAG AND DROP

//TODO: ONLY BE ABLE TO PLAY ON YOUR OWN BOARD, PLAYER ORDER (this will probably "fix"
// an insane amount of bugs)
