      console.log(`CANASTA: ${props.room["users"][usr]["canasta"]}`)
      var columnas = "0"
      if(props.room["users"][usr]["canasta"]){
        var columnas = props.room["users"][usr]["canasta"].toString()
      }
      boards.push(<Player fichas={props.room["users"][usr]["canasta"]}
                          columnas={props.room["users"][usr]["columnas"]}
                          socket={client} 
                          roomname={roomname}
                          puntuacion={props.room["users"][usr]["puntuacion"]}
                          fichasEnCuarto={props.room["fichas"]}
                          tablero={props.room["users"][usr]["tablero"]}
                          key = {props.room["users"][usr]["user"]}/>)
      boards.push(<p>COLUMNAS: {columnas}</p>)
    }
